(function () {
    "use strict";

    angular
        .module("ineel.services")
        .factory('PermisosService', ['$http', '$q', 'globalGet', 'AuthService', 'localStorageService', 'MenuService', PermisosService]);

    function PermisosService($http, $q, globalGet, AuthService, localStorageService, MenuService) {
        var service = {};
        var listafuncionesadmin = MenuService.getMenuAdmin();
        var listafuncionesch = MenuService.getMenuCH();
        var listafuncionesmt = MenuService.getMenuMT();
        var listafuncionescr = MenuService.getMenuCR();
        var listafunciones = null;

        service.verificaPermisos = function (siguiente, modulo) {
            switch (modulo) {
                case 'ADM':
                    listafunciones = listafuncionesadmin;
                    break;
                case 'CH':
                    listafunciones = listafuncionesch;
                    break;
                case 'CR':
                    listafunciones = listafuncionescr;
                    break;
                case 'MT':
                    listafunciones = listafuncionesmt;
                    break;

                default:
                    break;
            }
            var q = $q.defer();
            try {
                var next = siguiente.replace(':id', '').replace(':seccion', '').replace('/:id2', '');

                var permitir = false;
                listafunciones.forEach(function (element) {
                    if (typeof element.funcion.url !== undefined) {
                        var r = element.funcion.url.split('#');
                        if (r[1] != null) {
                            if (r[1].indexOf(next) >= 0 || next === '/home') {
                                permitir = true; return;
                            }
                        }
                    }
                });

                q.resolve(permitir);

            } catch (error) {
                q.resolve(permitir);
            }
            return q.promise;
        };

        return service;

    }

})();
(function () {
    "use strict";

    var app = angular.module("ineel.services");

    app.factory('authInterceptorService', ['$q', '$location', 'localStorageService', authInterceptorService]);

    function authInterceptorService($q, $location, localStorageService) {

        var authInterceptorServiceFactory = {};

        var _request = function (config) {

            config.headers = config.headers || {};
            //config.headers.UsuarioId = {};
            //config.headers.NombreCompleto = {};
            var authData = localStorageService.get('authorizationData');
            if (authData) {
                config.headers.Authorization = 'Bearer ' + authData.token;
                try {
                    if (typeof authData.userprofile != "undefined") {
                        if (authData.userprofile != null) {
                            config.headers.UsuarioId = authData.userprofile.clavePersona;
                            config.headers.NombreCompleto = authData.userprofile.nombreCompleto;
                        }
                    }
                    //config.headers.protocol = $location.protocol();
                    //config.headers.host = $location.host();
                    //config.headers.port = $location.port();                    

                } catch (e) {
                    console.log("errAch");
                    console.log(e);
                }
            }

            return config;
        }

        var _responseError = function (rejection) {
            //console.log(rejection);
            if (rejection.data != null)
                console.log(rejection);
            if (rejection.status < 0) {
                toastr.error("se ha negado la conexión", "Verificar la disponibilidad del servidor");
            } else
                if (rejection.status === 401) {
                    $location.path('/login');
                } else if (rejection.status === 400) {
                    console.log("400: " + rejection);
                }
            return $q.reject(rejection);
        }

        authInterceptorServiceFactory.request = _request;
        authInterceptorServiceFactory.responseError = _responseError;

        return authInterceptorServiceFactory;
    }

})();

